setTimeout(function(){var e=document.querySelector("html").dataset.store;fetch("busca_noticias.php?loja="+e,{method:"GET",mode:"cors"}).then(function(e){e.text().then(e=>{var t="";for(i=e.search('<ul class="noticias">');i<e.search("</li></ul>        </div>");i++)t+=e[i];document.querySelector(".news__content").innerHTML=t+"</li></ul>"})})},300),setTimeout(function(){if(document.querySelectorAll(".news__content li").length>3){var e=document.querySelectorAll(".news__content li");for(i=3;i<e.length;i++)e[i].remove()}},1500);